import { vc_payload } from "./vc_payload";

const dateToJtc = (dateUTC: Date) => {
  // JST（日本標準時）に変換
  // UTCからJSTは+9時間なので、それをミリ秒で計算
  const JST_OFFSET = 9 * 60 * 60 * 1000; // 9 hours in milliseconds
  return new Date(dateUTC.getTime() + JST_OFFSET);
};

